"use strict";

// long form
angular.module("MyMod").controller("MyCtrl", ['$a', '$b', function($a, $b) {
}]);

// w/ dependencies
angular.module("MyMod", ["OtherMod"]).controller("MyCtrl", ['$c', '$d', '$e', '$f', '$g', '$h', '$i', function($c, $d, $e, $f, $g, $h, $i) {
}]);

// simple
myMod.service("$a", ['$b', function($b) {
}]);
myMod.controller("foo", ['$a', '$b', function($a, $b) {
}]);
myMod.service("foo", ['$c', '$d', function($c, $d) {
}]);
myMod.factory("foo", ['$e', '$f', function($e, $f) {
}]);
myMod.directive("foo", ['$g', '$h', function($g, $h) {
}]);
myMod.filter("foo", ['$i', '$a', function($i, $a) {
}]);
myMod.animation("foo", ['$b', '$c', function($b, $c) {
}]);
myMod.invoke("foo", ['$d', '$e', function($d, $e) {
}]);

// implicit config function
angular.module("MyMod", ["OtherMod"], ['$interpolateProvider', function($interpolateProvider) {}]).controller("foo", ['$f', function($f) {}]);

// object property
var myObj = {};
myObj.myMod = angular.module("MyMod");
myObj.myMod.controller("foo", ['$g', '$h', function($g, $h) { a }]);

// run, config don't take names
myMod.run(['$i', '$a', function($i, $a) {
}]);
angular.module("MyMod").run(['$b', function($b) {
}]);
myMod.config(['$c', '$d', function($c, $d) {
}]);
angular.module("MyMod").config(function() {
});

// directive return object
myMod.directive("foo", ['$e', function($e) {
    return {
        controller: ['$f', '$g', function($f, $g) {
            bar;
        }]
    }
}]);
myMod.directive("foo", ['$h', function($h) {
    return {
        controller: function() {
            bar;
        }
    }
}]);

// provider, provider $get
myMod.provider("foo", ['$i', function($i) {
    this.$get = ['$a', '$b', function($a, $b) {
        bar;
    }];
    self.$get = ['$c', function($c) {}];
    that.$get = ['$d', function($d) {}];
    ignore.$get = function($e) {};
}]);
myMod.provider("foo", function() {
    this.$get = ['$f', function($f) {
        bar;
    }];
});
myMod.provider("foo", function() {
    return {
        $get: ['$g', '$h', function($g, $h) {
            bar;
        }]};
});
myMod.provider("foo", {
    $get: ['$i', function($i) {
        bar;
    }]
});
